import React from 'react';
import { useSpring, animated } from '@react-spring/web';

function App() {
  // Анимация появления формы
  const formAnimation = useSpring({
    from: { opacity: 0, transform: 'translateY(-30px)' },
    to: { opacity: 1, transform: 'translateY(0)' },
    config: { tension: 150, friction: 20 },
  });

  // Анимация кнопки при наведении
  const [hoverAnimation, setHoverAnimation] = useSpring(() => ({
    scale: 1,
    config: { tension: 300, friction: 10 },
  }));

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500">
      <animated.div
        style={formAnimation}
        className="w-full max-w-md p-8 bg-white rounded-xl shadow-xl"
      >
        <h1 className="text-3xl font-bold text-center text-gray-800">Добро пожаловать!</h1>
        <p className="mt-2 text-sm text-center text-gray-600">
          Войдите в свой аккаунт
        </p>
        <form className="mt-6">
          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-600" htmlFor="email">
              Электронная почта
            </label>
            <input
              id="email"
              type="email"
              placeholder="Введите вашу почту"
              className="w-full px-4 py-2 mt-2 text-gray-700 bg-gray-100 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:ring-blue-300"
            />
          </div>
          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-600" htmlFor="password">
              Пароль
            </label>
            <input
              id="password"
              type="password"
              placeholder="Введите ваш пароль"
              className="w-full px-4 py-2 mt-2 text-gray-700 bg-gray-100 border border-gray-300 rounded-lg focus:outline-none focus:ring focus:ring-blue-300"
            />
          </div>
          <animated.button
            type="submit"
            style={hoverAnimation}
            onMouseEnter={() => setHoverAnimation.start({ scale: 1.05 })}
            onMouseLeave={() => setHoverAnimation.start({ scale: 1 })}
            className="w-full px-4 py-2 text-white bg-blue-500 rounded-lg shadow-lg hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-300"
          >
            Войти
          </animated.button>
        </form>
        <p className="mt-4 text-sm text-center text-gray-600">
          Еще нет аккаунта?{' '}
          <a href="#" className="text-blue-500 hover:underline">
            Зарегистрируйтесь
          </a>
        </p>
      </animated.div>
    </div>
  );
}

export default App;
