import React, { useState } from "react";
import { motion } from "framer-motion"; // Для анимаций

const AuthForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    // Эмуляция отправки данных
    setTimeout(() => {
      setIsSubmitting(false);
      alert("Авторизация прошла успешно!");
    }, 2000);
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gradient-to-r from-purple-500 to-indigo-600">
      <motion.div
        className="w-full max-w-sm p-8 bg-white rounded-lg shadow-xl transform transition-all duration-500 hover:scale-105"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h2 className="text-4xl font-bold text-center text-purple-600 mb-6">Авторизация</h2>

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-700">Email</label>
            <motion.input
              type="email"
              className="w-full mt-2 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-300 transform hover:scale-105"
              placeholder="Введите ваш email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              whileHover={{ scale: 1.02 }}
              whileFocus={{ scale: 1.05 }}
            />
          </div>

          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-700">Пароль</label>
            <motion.input
              type="password"
              className="w-full mt-2 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-300 transform hover:scale-105"
              placeholder="Введите ваш пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              whileHover={{ scale: 1.02 }}
              whileFocus={{ scale: 1.05 }}
            />
          </div>

          <motion.button
            type="submit"
            className={`w-full py-3 bg-purple-600 text-white rounded-lg font-semibold transition-all duration-300 hover:bg-purple-700 transform hover:scale-105 ${isSubmitting ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={isSubmitting}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {isSubmitting ? "Авторизация..." : "Войти"}
          </motion.button>
        </form>

        <div className="mt-4 text-center">
          <p className="text-sm text-gray-500">Не зарегистрированы? <a href="#" className="text-purple-600 hover:underline">Зарегистрироваться</a></p>
        </div>
      </motion.div>
    </div>
  );
};

export default AuthForm;
