import React, { useState } from "react";
import { motion } from "framer-motion"; // Для анимаций

const AuthForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    // Эмуляция отправки данных
    setTimeout(() => {
      setIsSubmitting(false);
      alert("Авторизация прошла успешно!");
    }, 2000);
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gradient-to-r from-purple-500 to-indigo-600">
      <motion.div
        className="w-full max-w-sm p-8 bg-white rounded-lg shadow-lg"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
      >
        <h2 className="text-3xl font-bold text-center text-purple-600 mb-6">Авторизация</h2>

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-700">Email</label>
            <input
              type="email"
              className="w-full mt-2 p-3 border rounded-lg border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              placeholder="Введите ваш email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-700">Пароль</label>
            <input
              type="password"
              className="w-full mt-2 p-3 border rounded-lg border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
              placeholder="Введите ваш пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <motion.button
            type="submit"
            className={`w-full py-3 bg-purple-600 text-white rounded-lg font-semibold transition-all duration-200 hover:bg-purple-700 ${isSubmitting ? "opacity-50 cursor-not-allowed" : ""}`}
            disabled={isSubmitting}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {isSubmitting ? "Авторизация..." : "Войти"}
          </motion.button>
        </form>
      </motion.div>
    </div>
  );
};

export default AuthForm;
